mapper.select:
SELECT POST_NO, TITLE, CONTENTS, BOARD_DATE, HIT,CATEGORY_NO, CASE WHEN LIKE_CNT IS NULL THEN 0 ELSE LIKE_CNT END AS LIKE_CNT, POST_CMT_CNT, MEM_NO, NIC, GRADE_NO FROM(SELECT B.POST_NO, B.CONTENTS, BC.CATEGORY_NO, CASE WHEN LENGTH(B.TITLE) > 25 THEN SUBSTR(B.TITLE,1 ,25) ELSE B.TITLE END AS TITLE, B.HIT,CASE WHEN POST_CMT_CNT IS NULL THEN 0 ELSE POST_CMT_CNT END AS POST_CMT_CNT , TO_CHAR(B.BOARD_DATE, 'YYYY-MM-DD') AS BOARD_DATE, M.MEM_NO, G.GRADE_NO, BLIKE.LIKE_CNT, M.NIC, B.DEL FROM BOARD B INNER JOIN MEM M ON B.MEM_NO = M.MEM_NO AND LEAVE_DATE IS NULL INNER JOIN BOARD_CATEGORY BC ON B.CATEGORY_NO = BC.CATEGORY_NO INNER JOIN GRADE G ON B.MEM_NO = G.MEM_NO INNER JOIN GRADE_CATEGORY GC ON G.GRADE_NO = GC.GRADE_NO LEFT OUTER JOIN (SELECT B.POST_NO, COUNT(*) AS POST_CMT_CNT FROM BOARD B INNER JOIN BOARD_CMT BC ON B.POST_NO = BC.POST_NO GROUP BY B.POST_NO) BCMT ON B.POST_NO = BCMT.POST_NO LEFT OUTER JOIN (SELECT POST_NO, COUNT(*) AS LIKE_CNT FROM BOARD_LIKE GROUP BY POST_NO)BLIKE ON B.POST_NO = BLIKE.POST_NO) WHERE DEL != 0
AND POST_NO= #{postNo}
AND POST_NO= #{newPostNo}

		SELECT *
    	FROM BOARD_LIKE
    	WHERE MEM_NO = #{loginUserNo}
    	AND POST_NO = #{postNo}
